import * as React from 'react';
import '../index.css';
import httpClient from '../httpClient';
import Spinner from './Spinner';
const config = require('../config.json');


class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: false,
            breaks: null
        }

        this.ensureDataFetched = this.ensureDataFetched.bind(this);
        this.setLoading = this.setLoading.bind(this);
    }


    setLoading(isLoading) {
        this.setState({ isLoading });
    }



    componentDidMount() {
        this.ensureDataFetched();
    }

    ensureDataFetched() {
        this.setLoading(true);
        const client = new httpClient();
        return client.get(`${config.aws.api.url}${config.aws.api.path}`, { 'x-api-key': config.aws.api.key })
            .then(response => {
                if (response.data && response.status === 200) {
                    this.setState((state) => {
                        return { state: state.breaks = response.data };
                    });
                }
                this.setLoading(false);
            })
            .catch((err) => { this.setLoading(false); });
    }

    updateRating(v, brId, demo) 
    {
        this.setState(prevState => ({
            breaks:prevState.breaks
            .map(p => p.Id == brId ? p.Ratings
                .map(d => d.DemoName == demoName ?{ ...d, Score: v}: d)  : p)
        }));




       const br = this.state.breaks.find( p => p.Id == brId);
         if(!br) return;
        const rating = br.Ratings.find(d => d.DemoName == demoName)
        if(!rating) return;
 




    }


    render() {
        return (
            <React.Fragment>
                <div className="row">
                    <div className="col-md-4">Break Id</div><div className="col-md-6">Commercial Demographic</div><div className="col-md-2">Rating</div>
                </div>
                {this.state.breaks && this.state.breaks.map((br, i) =>

                    <div key={i}>
                        <div className="row">
                            <div key={i} className="col-md-4" >{br.Id}</div>

                            <div key={i+1} className="col-md-6">
                                {br.Ratings.map((rating, j) =>
                                    <div key={j} className="col-md-12">{rating.DemoName}</div>
                                )}
                            </div>

                            <div key={i+2} className="col-md-2">
                                {br.Ratings.map((rating, k)=>
                                 <input key={k+1} type="text" onChange={(e) => this.updateRating(e.target.value, br.Id, rating.DemoName)} className="col-md-12" defaultValue={rating.Score} placeholder={rating.Score} />
                                )}
                            </div>

                        </div>
                    </div>

                )}



                {this.state.isLoading
                    ? <Spinner isLoading={this.state.isLoading} />
                    : <input type="button" onClick={this.ensureDataFetched} value="get data"></input>}
            </React.Fragment>
        )
    };
};

export default Home;
