import * as React from 'react';
import '../index.css';
import httpClient from '../httpClient';
import Spinner from './Spinner';
const config = require('../config.json');


class Home extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: false
        }
    }



