        this.ensureDataFetched();
    }

    ensureDataFetched() {

        this.setLoading(true);


        const client = new httpClient();
        return client.get(`${config.aws.vehiclesApi.url}${config.aws.vehiclesApi.makesPath}`, { 'x-api-key': config.aws.vehiclesApi.key })
            .then(response => {
                const data = JSON.parse(response.data.body);

                if (data && response.data && response.status === 200) {

                    this.setState((state) => {
                        return { state: state.data = data };
                    });


                }

                this.setLoading(false);
            });

    }

    render() {
        return (
            <React.Fragment>
                <div>
                    <h1>Hello, world!</h1>
                    <input type="button" onClick={this.ensureDataFetched}></input>
                </div>
                <Spinner isLoading={this.state.isLoading} />
            </React.Fragment>
        )
    };
};

export default Home;
